let para1 = document.querySelector("#para");
let btn1 = document.querySelector("#btn");

btn1.addEventListener("click", () => {
    if (para1.style.display === "none") {
        para1.style.display = "block";   // देखिने
    } else {
        para1.style.display = "none";    // लुक्ने
    }
});
